import React, { FormEvent, useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import './styles.css';

import api from '../../services/api';
import logo from '../../assets/images/github.png';
import brazilFlag from '../../assets/images/brazil-flag.png';
import usaFlag from '../../assets/images/usa-flag.png';
import pet from '../../assets/images/Octocat.png';

import { MdSend } from 'react-icons/md';
import GitHubProfile, { Profile } from '../../components/GitHubProfile';



const Landing: React.FC = () => {

  const [searched, setSearched] = useState(false);
  const [username, setUsername] = useState('');

  const [profile, setProfile] = useState<Profile>({
    name: '',
    username: '',
    about: '',
    company: '',
    twitter: '',
    blog: '',
    country: '',
    repositories: 0,
    followers: 0,
    link: ''
  });

  function toastNotify(text: string) {
    toast.error(text, {
      position: "top-right",
      autoClose: 2500,
      style: {
        fontFamily: 'Poppins',
        fontWeight: 200,
      },
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: false,
      draggable: true,
      progress: undefined,
    });
  }

  async function handleSubmit(e: FormEvent) {
    e.preventDefault();
    if (username) {

      await api.get(`/${username}`).then(response => {

        setSearched(true);
        const ProfileFromRequest: Profile = response.data;

        setProfile(ProfileFromRequest);

      }).catch(err => {

        toastNotify(`${username} does not exist in github`);

      });

    }
  }

  return (

    <div className="container">

      <ToastContainer />

      <div id="header">
        <img className="logo" src={logo} alt="github user checker" />
        <div id="languages">
          <img className="flag" src={brazilFlag} alt="Change language to Portuguese" />
          <img className="flag" src={usaFlag} alt="Change language to English" />
        </div>
      </div>

      <div id="content">

        {searched ? (
          <GitHubProfile profile={profile} />
        ) : searched}

        <div className={`search ${searched ? 'searched' : ''}`}>
          <img className="pet" src={pet} alt="Pet"/>
          <form>
            <input onChange={e => setUsername(e.target.value)} type="text" placeholder="WHO'S THE ONE NOW BRO?" />
            <button type="submit" onClick={handleSubmit} className="input-button">
              <MdSend className="input-icon" />
            </button>
          </form>
        </div>

      </div>

      <div id="footer">
        <h3>© 2020 whitshs5g All Rights Reserved</h3>
      </div>

    </div>
  )
}

export default Landing;
