import React, { useState } from 'react';
import './styles.css';
import api from '../../services/api';

import logo from '../../assets/images/github.png';
import brazilFlag from '../../assets/images/brazil-flag.png';
import usaFlag from '../../assets/images/usa-flag.png';
import pet from '../../assets/images/Octocat.png';

import { MdSend } from 'react-icons/md';

const Landing: React.FC = () => {

  const [searched, setSearched] = useState(false);
  const [username, setUsername] = useState('');

  async function handleSubmit() {

    const data = await api.get(`${username}`);
    if (data.status === 404) {
      alert('Not found');
    } 

  }

  return (
    <div className="container">

      <div id="header">
        <img className="logo" src={logo} alt="github user checker" />
        <div id="languages">
          <img className="flag" src={brazilFlag} alt="Change language to Portuguese" />
          <img className="flag" src={usaFlag} alt="Change language to English" />
        </div>
      </div>

      <div id="content">
        <div className={`search ${searched ? 'searched' : ''}`}>
          <img className="pet" src={pet} alt="Pet"/>
          <form>
            <input onChange={e => setUsername(e.target.value)} type="text" placeholder="WHO'S THE ONE NOW BRO?" />
            <MdSend onClick={handleSubmit} className="input-icon" />
          </form>
        </div>

      </div>

      <div id="footer">
        <h3>© 2020 whitshs5g All Rights Reserved</h3>
      </div>

    </div>
  )
}

export default Landing;
